JOIN all_creatures c ON c.id = v.creature_id
ON DUPLICATE KEY UPDATE
    name = VALUES(name),
    image = VALUES(image),
    experience = VALUES(experience),
    lifepoint_exp = VALUES(lifepoint_exp),
    slayer_exp = VALUES(slayer_exp),
    level = VALUES(level),
    slayer_level = VALUES(slayer_level),
    lifepoints = VALUES(lifepoints),
    defence = VALUES(defence),
    max_melee = VALUES(max_melee),
    max_ranged = VALUES(max_ranged),
    max_magic = VALUES(max_magic),
    max_necromancy = VALUES(max_necromancy),
    max_spec = VALUES(max_spec),
    updated_at = NOW();
